    constructor(prop){
        super(prop);
        this.state = {
            coin: []
        };
    }

    componentDidMount(){
        let config = {
            'Authorization': '776194b8-0b73-4227-9b92-cc67fbed4dcf',
            'Access-Control-Allow-Origin': '*',
            crossdomain: true
        };

        axios.get('https://pro-api.coinmarketcap.com/v1/cryptocurrency/info', {headers: config})
            .then(response => {
                console.log(response);
                this.setState({
                    coin: response.data[0]
                })
