import "../coinPage.css";
import React, { Component } from "react";

class Ind extends Component {
    constructor() {
        super();
        this.state = {
          coin: [],
          error: null,
          coinlist: true
        };
    }

  componentDidMount() {
    fetch("https://api.coinmarketcap.com/v1/ticker/?convert=USD&limit=15")
      .then(res => res.json())
      .then(
        result => {
          this.setState({
            coin: result,
            coinlist: true
          });
        },

        error => {
          this.setState({
            error,
            coinlist: false
          });
        }
      );
  }

  render() {

    if (this.state.error) {
        return <div>Error: {this.state.error.message}</div>;
      } else if (this.state.coinlist) {
        info = (
            <div className="indivcoin">
            <p>(icon) {this.state.coin[0].name}  {this.state.coin[0].symbol}</p>
            <p>Time</p>
            <p>Latest Price:</p>
            <p>{this.state.coin[0].price_usd} {this.state.coin[0].percent_change_1h}</p>
            <p>{this.state.coin[0].price_btc}</p>
            <p>MarketCap: {this.state.coin[0].market_cap_usd}</p>
            <p>(24h)Volume: {this.state.coin[0]["24h_volume_usd"]}</p>
            <p>Current Supply: {this.state.coin[0].total_supply}</p>
            <p>Total Supply: {this.state.coin[0].max_supply}</p>
          </div>
        );
      }



    return (
      <div className="indivcoin">

      </div>
    );
  }
}

export default Ind;
