import React, { Component } from "react";
import axios from "axios";
import "./NC.css";

class NC extends Component {
  constructor(){
    super();
    this.state ={
      news: [],
      comment: [],
      newsShow: true,
      commentShow: false
    }
  }

  componentDidMount(){
    var api_key = process.env.API_NEWS_KEY;
    axios.defaults.headers.common['Authorization'] = "MY-API-KEY";
    axios.get(`https://cryptocontrol.io/api/v1/public/news/coin/bitcoin?latest=true`, {headers: api_key})
        .then(response => {
            console.log(response);
            this.setState({
              news: response
            })
        }).catch(() => console.log('Someting going wrong'));
}

  buttonnewsClick = () => {
    this.setState({
      news: [],
      newsShow: true,
      commentShow: false
    })
  }

  buttoncommentClick = () => {
    this.setState({
      comment: [],
      newsShow: false,
      commentShow: true
    })
  }

  render() {
    let info  = null;
    if (this.state.newsShow){
      info = (
        <div>

        </div>
      )
    }else if(this.state.commentShow){
      info = (
        <div>
          <p>yup</p>
        </div>
      )
    }

    return (
      <div className="infoboard">
        <button onClick={this.buttonnewsClick}>News</button>
        <button onClick={this.buttoncommentClick}>Comment</button>
        {info}
      </div>
    )
  }
}

export default NC;
