import Dashboard from './Dashboard/Dashboard'
import React, { Component } from 'react';
import './App.css';

class App extends Component {
  state = {
    coin: [
      {
        "id": "bitcoin", 
        "name": "Bitcoin", 
        "symbol": "BTC", 
        "rank": "1", 
        "price_usd": "3667.81756186", 
        "price_btc": "1.0", 
        "24h_volume_usd": "6520931125.43", 
        "market_cap_usd": "64093002898.0", 
        "available_supply": "17474425.0", 
        "total_supply": "17474425.0", 
        "max_supply": "21000000.0", 
        "percent_change_1h": "-0.16", 
        "percent_change_24h": "-9.11", 
        "percent_change_7d": "-4.91", 
        "last_updated": "1547143943"
    }, 
    {
        "id": "ripple", 
        "name": "XRP", 
        "symbol": "XRP", 
        "rank": "2", 
        "price_usd": "0.3303075156", 
        "price_btc": "0.00009033", 
        "24h_volume_usd": "827907535.982", 
        "market_cap_usd": "13555954248.0", 
        "available_supply": "41040405095.0", 
        "total_supply": "99991724864.0", 
        "max_supply": "100000000000", 
        "percent_change_1h": "0.71", 
        "percent_change_24h": "-9.84", 
        "percent_change_7d": "-8.68", 
        "last_updated": "1547143921"
    }
    ],
    coinlist: true
  }



  render() {
    let coins = null;

    if (this.state.coinlist) {
      coins = (
        <div>
          {/* the index can indentify what conponent u have clicked */}
          {this.state.coin.map((coin, index) => {
            return <Dashboard 
            rank={coin.rank} 
            name={coin.name} 
            marketcap={coin.market_cap_usd} 
            price={coin.price_usd} 
            change={coin.percent_change_24h}
            vol={"4567889"}
            supply={coin.total_supply} /> 
          })}
        </div>
      );
    }


    return (
      <div className="App">
      {coins}
      </div>
    );
  }
}

export default App;
