import "../coinPage.css";
import React, { Component } from "react";

class Ind extends Component {
    constructor() {
        super();
        this.state = {
          coin: [],
          error: null,
          coinlist: true
        };
    }

  componentDidMount() {
    fetch("https://api.coinmarketcap.com/v1/ticker/?convert=USD&limit=15")
      .then(res => res.json())
      .then(
        result => {
          this.setState({
            coin: result,
            coinlist: true
          });
        },

        error => {
          this.setState({
            error,
            coinlist: false
          });
        }
      );
  }

  render() {

    if (this.state.error) {
        return <div>Error: {this.state.error.message}</div>;
