import React, { Component } from "react";
import "./NC.css"

class NC extends Component {
  constructor(){
    super();
    this.state ={
      news: [],
      comment: [],
      newsShow: true,
      commentShow: false
    }
  }

  buttonnewsClick = () => {
    this.setState({
      news: [],
      newsShow: true,
      commentShow: false
    })
  }

  buttoncommentClick = () => {
    this.setState({
      comment: [],
      newsShow: false,
      commentShow: true
    })
  }

  render() {
    let info  = null;
    if (this.state.newsShow){
      info = (
        <div>
          <p>hello</p>
        </div>
      )
    }else if(this.state.commentShow){
      info = (
        <div>
          <p>yup</p>
        </div>
      )
    }

    return (
      <div>
        <button onClick={this.buttonnewsClick}>News</button>
        <button onClick={this.buttoncommentClick}>Comment</button>
        {info}
      </div>
    )
  }
}

export default NC;
